import React, { useEffect, useState } from "react";
import axios from "axios";

const handleAudioRecognition = () => {
  const input = "\\frontend\\audio\shrag.ogg";
  const formData = new FormData();
  formData.append("audio", input);

  fetch("http://localhost:8000/recognize-audio/", {
    method: "POST",
    body: formData,
  })
    .then((response) => response.json())
    .then((data) => {
      console.log("Recognized text:", data.text);
      // Do something with the recognized text in your React application
    })
    .catch((error) => {
      console.error("Error:", error);
    });
};

export default handleAudioRecognition;
//recognize-audio/
//http://localhost:8000/api/todos/
