import React, { Component } from "react";
import Modal from "./components/Modal";
import axios from "axios";

class App extends Component {
  state = {
    viewCompleted: false,
    activeItem: {
      title: "",
      description: "",
      completed: false,
    },
    todoList: [],
  };

  async componentDidMount() {
    try {
      await this.fetchTodoList();
      this.startRefreshing();
    } catch (error) {
      console.log(error);
    }
  }

  fetchTodoList = async () => {
    const res = await fetch("http://localhost:8000/api/todos/");
    const todoList = await res.json();
    this.setState({ todoList });
  };

  startRefreshing = () => {
    this.refreshInterval = setInterval(this.fetchTodoList, 5000);
  };

  stopRefreshing = () => {
    clearInterval(this.refreshInterval);
  };

  toggle = () => {
    this.setState({ modal: !this.state.modal });
  };

  handleSubmit = (item) => {
    this.toggle();
    if (item.id) {
      axios.put(`http://localhost:8000/api/todos/${item.id}/`, item);
    } else {
      axios.post("http://localhost:8000/api/todos/", item);
    }
  };

  handleDelete = (item) => {
    axios.delete(`/api/todos/${item.id}/`).then((res) => this.refreshList());
  };
  createItem = () => {
    const item = { title: "", description: "", completed: false };
    this.setState({ activeItem: item, modal: !this.state.modal });
  };

  displayCompleted = (status) => {
    this.setState({ viewCompleted: status });
  };

  renderTabList = () => {
    const { viewCompleted } = this.state;
    return (
      <div className="my-5 tab-list">
        <button
          onClick={() => this.displayCompleted(true)}
          className={viewCompleted ? "active" : ""}
        >
          Complete
        </button>
        <button
          onClick={() => this.displayCompleted(false)}
          className={viewCompleted ? "" : "active"}
        >
          Incomplete
        </button>
      </div>
    );
  };

  renderItems = () => {
    const { viewCompleted, todoList } = this.state;
    const newItems = todoList.filter(
      (item) => item.completed === viewCompleted
    );
    return newItems.map((item) => (
      <li
        key={item.id}
        className="list-group-item d-flex justify-content-between align-items-center"
      >
        <span
          className={`todo-title mr-2 ${viewCompleted ? "completed-todo" : ""}`}
          title={item.description}
        >
          {item.title}
        </span>
      </li>
    ));
  };

  componentWillUnmount() {
    this.stopRefreshing();
  }

  render() {
    return (
      <main className="content">
        <h1 className="text-white text-uppercase text-center my-4">Todo App</h1>
        <div className="row">
          <div className="col-md-6 col-sm-10 mx-auto p-0">
            <div className="card p-3">
              <div className="">
                <button onClick={this.createItem} className="btn btn-success">
                  Add Task
                </button>
              </div>
              {this.renderTabList()}
              <ul className="list-group list-group-flush">
                {this.renderItems()}
              </ul>
            </div>
          </div>
        </div>
        {this.state.modal ? (
          <Modal
            activeItem={this.state.activeItem}
            toggle={this.toggle}
            onSave={this.handleSubmit}
          />

        ) : null}
      </main>
    );
  }
}

export default App;
